import Link from 'next/link';
import Layout from '../components/Layout';

const News = ({ news }) => (
    <Layout>
        <h2> List of News</h2>
        <hr />
        {news.map((item, i) => (
                <p key={i}><a href={item.url}>{item.title}</a></p>
        ))}
    </Layout>
);

News.getInitialProps = async () => {
    let news;
    try {
        const res = await fetch('https:/hn.algolia.com/api/v1/search?query=react');
        news = await res.json();
        console.log(news.hits);
    } catch (error) {
        console.log(`There was an error ${error}`)
        news = [];
    }
    return {
        news: news.hits
    };
};

export default News;
